package subsidiesreg

import (
	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/common/hexutil"
)

// GetContractImplBin is Subsidies Registry implementation bin code
func GetContractImplBin() []byte {
	return hexutil.MustDecode("")
}

// GetContractProxyBin is Subsidies Registry implementation bin code
func GetContractProxyBin() []byte {
	return hexutil.MustDecode("")
}

// ContractImplAddress is the Subsidies Registry implementation address
var ContractImplAddress = common.HexToAddress("0x5e8d2b3d4DDDc52231D0B8BE701767Da3Ea33404")

// ContractAddress is the Subsidies Registry proxy address
var ContractAddress = common.HexToAddress("0x5136b6CA83D1F2bd94Cb352855DC215CbfCE5942")

// ImplSlotId in the proxy contract storage refers to the address of the implementation
// (This is the keccak-256 hash of "eip1967.proxy.implementation" subtracted by 1)
var ImplSlotId = common.HexToHash("0x360894a13ba1a3210667c828492db98dca3e2076cc3735a920a3ca505d382bbc")

// InitializeCalldata represents the call of the initialize() method
var InitializeCalldata = hexutil.MustDecode("0x8129fc1c")
